import React , { Component } from 'react'
import PropTypes from 'prop-types'
import { connect } from './react-redux'

class ThemeSwitch extends Component {

    static contextTypes = {
        themeColor : PropTypes.string ,
        onSwitchColor : PropTypes.func
    }

    // constructor(){
    //     super()
    //     this.state = { themeColor : '' }
    // }

    // componentWillMount(){
    //     const { store } = this.context
    //     this._updateThemeColor()
    //     store.subscribe(() => this._updateThemeColor())
    // }

    handleSwitchColor(color){
        // console.log('color=' + color)
        // const { store } = this.context
        // store.dispatch({
        //     type : 'CHANGE_COLOR',
        //     themeColor : color
        // })

        if(this.props.onSwitchColor){
            this.props.onSwitchColor(color)
        }
    }

    // _updateThemeColor(){
    //     const { store } = this.context
    //     const state = store.getState()
    //     this.setState({ themeColor : state.themeColor })
    // }

    render(){
        return (
            <div>
                <button
                    onClick={this.handleSwitchColor.bind(this,'red')} 
                    style={{ color : this.props.themeColor }} >Red</button>
                <button 
                    onClick={this.handleSwitchColor.bind(this,'green')}
                    style={{ color : this.props.themeColor }} >Green</button>
            </div>
        )
    }
}

// 告诉高阶组件 connect ，根据 state 如何获取需要的属性
const mapStateToProps = (state) => {
    return {
        themeColor : state.themeColor
    }
}

// 告诉高阶组件 connect , 根据 dispatch 执行哪些更新 state 状态的 action 
const mapDispatchToProps = (dispatch) =>{
    return {
        onSwitchColor : (color) => {
            dispatch({ type : 'CHANGE_COLOR' , themeColor : color })
        }
    }
}

ThemeSwitch = connect(mapStateToProps , mapDispatchToProps)(ThemeSwitch)
export default ThemeSwitch
